css.content:
$vendor-prefixes: '-webkit-' '-moz-' '';

@mixin vendor($list) {
  @if length(nth($list, 1)) != 2 { $list: append((), $list); }
  @each $l in $list {
    $prop: nth($l, 1);
    $value: nth($l, 2);
    @each $p in $vendor-prefixes {
      #{$p + $prop}: $value;
    }
  }
}
@mixin vendor-prop($prop, $value) {
  @each $p in $vendor-prefixes {
    #{$prop}: #{$p + $value};
  }
}

@mixin only-webkit {
  @media screen and (-webkit-min-device-pixel-ratio: 0) {
    @content;
  }
}

@mixin redraw($target: body) {
  @-webkit-keyframes redraw {
    from { min-width: 1px; }
    to { min-width: 2px; }
  }
  #{$target} {
    -webkit-animation: redraw 1s infinite;
  }
}

/* main */
@include redraw;

.slider {
  width: 110px;
  height: 20px;
  margin-left: -90px;
  resize: horizontal;
  overflow: auto;
  @include vendor-prop(
    background-image, linear-gradient(rgba(255, 255, 255, 0.5), white)
  );
  background-size: 20px 20px;
  background-repeat: no-repeat;
  background-position: 100% 100%;

  &:hover {
    cursor: e-resize;
  }
}

#slider1 {
  min-width: 110px;
  max-width: 590px;

  &[style*="width: 1"] ~ .slides {
    margin-left: 0px;
  }
  &[style*="width: 2"] ~ .slides {
    margin-left: -500px;
  }
  &[style*="width: 3"] ~ .slides {
    margin-left: -1000px;
  }
  &[style*="width: 4"] ~ .slides {
    margin-left: -1500px;
  }
  &[style*="width: 5"] ~ .slides,
  &[style*="width: 6"] ~ .slides,
  &[style*="width: 7"] ~ .slides,
  &[style*="width: 8"] ~ .slides,
  &[style*="width: 9"] ~ .slides {
    margin-left: -2000px;
  }
}

.slides-container {
  width: 500px;
  height: 380px;
  overflow: hidden;

  $gradient:
    crimson, crimson 20%,
    orange 20%, orange 40%,
    seagreen 40%, seagreen 60%,
    skyblue 60%, skyblue 80%,
    slateblue 80%, slateblue;
  background-image: -webkit-linear-gradient(left, $gradient);
  background-image: -moz-linear-gradient(to right, $gradient);
  background-image: linear-gradient(to right, $gradient);
}
.slides {
  margin: 0;
  padding: 0;
  width: 2500px;
  list-style-type: none;
  @include vendor(
    (transition, margin-left 400ms ease-out)
  );

  li {
    float: left;
    width: 500px;
    height: 360px;
  }
}
.slide {
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  width: 100%;
  height: 100%;
  padding: 20px;
}
#page1 { background: crimson; color: white; }
#page2 { background: orange; color: black; }
#page3 { background: seagreen; color: white; }
#page4 { background: skyblue; color: black; }
#page5 { background: slateblue; color: white; }
