css.content:
/* #!scss */

$vendor-prefixes: '-webkit-' '-moz-' '-ms-' '-o-' '' !default;
// add vendor prefixes
@mixin vendor($list) {
  @if length(nth($list, 1)) != 2 { $list: append((), $list); }
  @each $l in $list {
    $prop: nth($l, 1);
    $value: nth($l, 2);
    @each $p in $vendor-prefixes {
      #{$p + $prop}: $value;
    }
  }
}

@mixin draggable-outer($width, $height, $style: both) {
  overflow: scroll;
  resize: $style;

  &::-webkit-scrollbar {
    width: $width;
    height: $height;
  }
  &::-webkit-resizer, &::-webkit-scrollbar-corner {
    visibility: hidden;
  }
}

// main

$vendor-prefixes: '-webkit-' '';
$shader:
  url(https://gist.github.com/xl1/3927259/raw/d4d9a4642cd1f9b8ec54b0d3209cc3828fa2d3ca/normal.vs)
  url(https://gist.github.com/xl1/3927259/raw/0f9375555e7a21ca2d64a36b6d4db12b9fccb8ce/golf.fs);

#field {
  @include draggable-outer(20px, 20px);

  // size
  border: solid;
  border-width: 300px 170px 0 190px;
  width: 20px;
  height: 11px;
  max-width: 200px;
  max-height: 100px;
  &[style] {
    border-left-width: 100px;
  }

  @include vendor(
    (animation, anim 7.5s cubic-bezier(0.1, 0.4, 0.8, 1.0))
    (animation-fill-mode, both)
    (animation-play-state, paused)
  );
  &[style]:not(:active) {
    @include vendor((animation-play-state, running));
  }
}

@-webkit-keyframes anim {
  0% {
    @include vendor((filter, custom($shader, timing 0)));
  }
  100% {
    @include vendor((filter, custom($shader, timing 9)));
  }
}
@keyframes anim {
  0% {
    @include vendor((filter, custom($shader, timing 0)));
  }
  100% {
    @include vendor((filter, custom($shader, timing 9)));
  }
}
