css.content:
/* #! scss */
@mixin draggable-outer($width, $height, $style: both) {
  overflow: scroll;
  resize: $style;

  &::-webkit-scrollbar {
    width: $width;
    height: $height;
  }
  &::-webkit-resizer, &::-webkit-scrollbar-corner {
    visibility: hidden;
  }
}


$resizer: 20px;
$domain-first-width: 210px;
$domain-min-width: 20px;
$domain-max-width: 410px;
$domain-first-height: 20px;
$domain-min-height: 20px;
$domain-max-height: 410px;

#wrap {
  display: -webkit-box;
  display: -webkit-flex;
  display: flex;
  width: $domain-first-width;
}

#handle + div {
  -webkit-box-flex: 1;
  -webkit-flex: 1 1 auto;
  flex: 1 1 auto;
  position: relative;
  margin: $resizer / 2;
  margin-left: $resizer / -2;
  background: url('../assets/he6nw');
  pointer-events: none;
}

#handle {
  @include draggable-outer($resizer, $resizer);
  -webkit-flex: 0 0 auto;
  flex: 0 0 auto;

  position: relative;
  background: {
    image: url('../assets/ekci0');
    repeat: no-repeat;
    position: $domain-first-width - $resizer / 2;
    size: -webkit-calc(100% - #{$domain-first-width}) -webkit-calc(100% - #{$resizer});
    size: calc(100% - #{$domain-first-width}) calc(100% - #{$resizer});
  }
  width: $domain-min-width;
  height: $domain-min-height;
  max-width: $domain-max-width;
  max-height: $domain-max-height;

  &:not([style]) {
    left: $domain-first-width - $domain-min-width;
    top: $domain-first-height - $domain-min-height;
  }
  &::-webkit-resizer {
    visibility: visible;
    border: 1px solid gray;
  }
}
