css.content:
#input {
    position:relative;
    overflow:hidden;
    min-width:100px;
    width:100px;
    height:10px;
    resize:horizontal;
    background:gray;
}
#input::after {
    content:"";
    display:block;
    position:absolute;
    right:0;
    top:0;
    width:10px;
    height:10px;
    background:black;
}
#output {
    margin-top:10px;
    width:100px;
    height:100px;
    background:url(../assets/6kmj4.png) no-repeat;
    -webkit-animation: redraw 1s infinite;
}
@-webkit-keyframes redraw {
    from { min-width: 1px; }
    to   { min-width: 2px; }
}

#input[style*='width: 10'] + #output {background-position:0px 0px;}
