css.content:
@-webkit-keyframes redraw {
  from { min-width: 10px; }
  to { min-width: 20px; }
}
body {
  margin: 0;
}
#source {
  padding: 32px 20% 0 5%;
  box-sizing: border-box;
  height: 3200px;
  font-size: 14px;
  line-height: 1.6;
  background-color: #999;
  background-image: -webkit-linear-gradient(white, white);
  background-image: linear-gradient(white, white);
  background-repeat: no-repeat;
  background-attachment: fixed;
  -webkit-box-reflect: below;
  -webkit-flow-into: f;
}
#source h1 {
  font-size: 2em;
}
#source section h1 {
  font-size: 1.5em;
}
#main {
  width: 100%;
  height: 3200px;
  -webkit-flow-from: f;
}
#preview {
  position: fixed;
  bottom: 100%;
  right: 0;
  width: 90%;
  height: 3200px;
  opacity: 0.6;
  -webkit-flow-from: f;
  -webkit-transform-origin: 100% 100%;
  -webkit-transform: rotateX(180deg) scale(0.145);
  -webkit-animation: redraw 1s infinite;
}
