css.content:
// math
$PI: 3.14159265;

@function sqrt($x) {
  @if $x == 0 { @return 0; }
  $s: 1;
  $t: 0;
  @while $t != $s { $t: $s; $s: ($s + $x / $s) / 2; }
  @return $s;
}
@function arctan($x) {
  @if $x == 0 { @return 0rad; }
  @if $x == 1/0 { @return 0.5rad * $PI; }
  @if $x == -1/0 { @return -0.5rad * $PI; }

  $r: $x > 1 or $x < -1;
  @if $r { $x: 1 / $x; }
  $a: 0;
  $i: 10;
  @while $i >= 1 {
    $a: $i * $i * $x * $x / (2 * $i + 1 + $a);
    $i: $i - 1;
  }
  @if $r {
    @return $x * ($PI / 2 / abs($x) - 1 / (1 + $a)) * 1rad;
  } @else {
    @return $x / (1 + $a) * 1rad;
  }
}
//

$scale: 2;
$tt: 1 - 1 / $scale;
$theta: arctan($tt);
$sy: sqrt(1 + $tt * $tt);

body {
    margin: 0;
    padding: 0;
    -webkit-perspective: 1000px;
    -webkit-perspective-origin: 0 0;
}

div {
    -webkit-transform-origin: 0 0;
}

#outer {
    width: 120px;
    height: 1000px;
    background: rgba(255, 200, 50, 0.2);
    -webkit-transform: scaleY($sy) rotateX($theta);
    -webkit-transform-style: preserve-3d;
}

#inner {
    width: 100px;
    height: 100px;
    background: red;
    position: sticky;
    top: 0;
    -webkit-transform: rotateX(-$theta) scaleY(1 / $sy);
}
