css.content:
// colors
$red: #f44;
$sky: #9cf;
$green: #4a4;
$blue: #46a;


$num-squares: 21;
$balloon-width: 400px;
$balloon-height: 160px;
$square-size: 100px;
$container-height: 465px;


body {
  overflow-x: hidden;
}
ol {
  list-style-type: none;
  margin: 0;
  padding: 0;
}

#container {
  position: absolute;
  width: 100%;
  height: $container-height;
  margin-top: -0.5 * $container-height;
  top: 50%;

  input, keygen, [_moz-type="-mozilla-keygen"] {
    display: none;
  }
}

#dice, .diceface {
  width: 100px;
  height: 100px
}
#dice {
  position: absolute;
  left: 50%;
  top: 100px;

  transform-origin: 0 0 0;
  transform: rotateX(-60deg) rotateY(-45deg);
  transform-style: preserve-3d;
}
.diceface {
  position: absolute;
  left: -50px;
  top: 0;

  background-image: url(../assets/jmIxZ);
  background-size: 600px 100px;
  background-position: 0 0;
}
#face1 {
  transform: translateZ(50px);
}
#face2 {
  background-position: -100px;
  transform: rotateX(90deg) translateZ(50px);
}
#face3 {
  background-position: -200px;
  transform: rotateY(90deg) translateZ(50px);
}

label {
  display: inline-block;
  background-color: $sky;
  padding: 5px 20px 5px 20px;
  border: 1px solid white;
  box-shadow: 0 0 0 1px $sky;

  &:hover {
    cursor: pointer;
  }
  &:active {
    background-color: $red;
    padding: 7px 20px 3px 20px;
  }
}

#submitbutton {
  display: block;
  width: 150px;
  text-align: center;
  margin: 250px auto 0;
}


#balloon {
  position: absolute;
  display: none;
  left: 50%;
  top: 150px;
  width: $balloon-width;
  margin-left: -0.5 * $balloon-width;
  overflow: hidden;
  border: 1px solid black;
  background-color: white;
}

#content {
  width: ($num-squares + 31 + 6) * $balloon-width;
  margin-left: -(31 + 5) * $balloon-width;
}

.content-list {
  width: $num-squares * $balloon-width;

  li {
    float: left;
    width: $balloon-width;
    height: $balloon-height;

    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-top: 20px;
    text-align: center;
    line-height: 25px;
  }
  strong {
    display: block;
    font-size: 35px;
    line-height: 40px;
    margin: 10px 0;
  }
}


#sugoroku {
  position: absolute;
  width: ($num-squares + 1 + 31 + 6) * $square-size;
  bottom: 0;
  left: 50%;
  margin-left: -(0.5 + 31 + 6) * $square-size;
}

.squares {
  width: ($num-squares + 1) * $square-size;
  height: $square-size;
  background-image: linear-gradient(to right,
    $green, $green $square-size, $sky $square-size,
    $sky $square-size * 2, $blue $square-size * 2, $blue);
  background-size: (3 * $square-size) $square-size;

  #start, #end {
    width: $square-size;
    height: $square-size;

    color: white;
    font-size: $square-size * 0.4;
    line-height: $square-size * 0.5;
    text-align: center;
    background-color: $red;
  }
  #start { float: left; }
  #end { float: right; }
}

#marker {
  position: absolute;
  bottom: -3px;
  left: 50%;
  margin-left: -0.5 * $square-size - 3px;
  width: $square-size;
  height: $square-size;
  border: 3px solid white;
  box-shadow: 0 0 0 3px $red;
}
.congrats {
  color: #f44;
}

// prevent overrun
#sugoroku, #balloon {
  div, ol {
    float: left;
  }
}
.cushion {
  height: 100px;
}
#cushion-square {
  min-width: 16 * $square-size;
}
#cushion-content {
  min-width: 16 * $balloon-width;
}

// document rules
#margin1-square  { width: $square-size; }
#margin1-content { width: $balloon-width; }
#margin2-square  { width: 2 * $square-size; }
#margin2-content { width: 2 * $balloon-width; }
#margin4-square  { width: 4 * $square-size; }
#margin4-content { width: 4 * $balloon-width; }
#margin8-square  { width: 8 * $square-size; }
#margin8-content { width: 8 * $balloon-width; }
#marginF-square  { width: 16 * $square-size; }
#marginF-content { width: 16 * $balloon-width; }
#margin6-square  { width: 6 * $square-size; }
#margin6-content { width: 6 * $balloon-width; }

@-moz-document regexp(".+\\Wat=[01]{4}1.*") {
  #margin1-square, #margin1-content { display: none; }
}
@-moz-document regexp(".+\\Wat=[01]{3}1.*") {
  #margin2-square, #margin2-content { display: none; }
}
@-moz-document regexp(".+\\Wat=[01]{2}1.*") {
  #margin4-square, #margin4-content { display: none; }
}
@-moz-document regexp(".+\\Wat=[01]1.*") {
  #margin8-square, #margin8-content { display: none; }
}
@-moz-document regexp(".+\\Wat=1.*") {
  #marginF-square, #marginF-content { display: none; }
}

$charranges: '[0-8]' '[9A-H]' '[I-S]' '[T-Za-d]' '[e-o]' '[p-z]';
@for $i from 1 through 6 {
  $regexp: '".+\\Wkey=[0-9A-z%]+#{nth($charranges, $i)}\\w\\w%3D%3D\\W.*"';
  @-moz-document regexp(#{$regexp}) {
    #face1 {
      background-position: 100px * -$i;
    }
    #face2 {
      background-position: 100px * (1 - $i);
    }
    #face3 {
      background-position: 100px * (2 - $i);
    }
    #balloon {
      display: block;
      animation: show-balloon 10ms (500ms + 300ms * $i) both;
    }
    #margin6-square {
      animation: move#{$i} (300ms * $i) 500ms both;
    }
    #margin6-content {
      width: (6 - $i) * $balloon-width;
    }
  }
}
input[name="at"]:checked ~ #balloon {
  display: none;
}

@for $i from 1 through 6 {
  @keyframes move#{$i} {
    0% { width: 6 * $square-size; }
    @for $j from 1 through $i {
      $ratio: 100% * $j / $i;
      $width: (6 - $j) * $square-size;
      #{$ratio - 5%} { width: $width; }
      #{$ratio}      { width: $width; }
    }
  }
}
@keyframes show-balloon {
  0% { opacity: 0; }
  100% { opacity: 1; }
}
