css.content:
/* #! scss */
/* remove unit */
@function num($x) {
  @return $x / ($x * 0 + 1);
}

$default-fragment-shader: unquote('mix(url(data:text/plain,) normal source-atop)');
// 省略できるはずだが、9/26 時点の Chrome では無理なので空シェーダを与える

$icons-top: 400px;
$icons-left: 150px 205px;
$icons-size: 50px;

input.controller {
  display: none;
}
label {
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  &:hover {
    cursor: pointer;
    opacity: 0.7; // 手抜き
  }
}

.container {
  position: relative;
}

.window-container {
  position: absolute;
  width: 1px; // なぜ要る？
  height: $icons-top;
  transition: filter 800ms cubic-bezier(0.3, 0.2, 0.7, 0.8);
  -webkit-transition: -webkit-filter 800ms cubic-bezier(0.3, 0.2, 0.7, 0.8);
}

.window {
  position: absolute;
  box-sizing: border-box;
  padding: 15px;
  background: #eee;
  border: 1px solid #aaa;
  border-radius: 4px;
}

.bar {
  height: 24px;
  line-height: 24px;
  font-size: 18px;
  margin: -15px;
  margin-bottom: 15px;
}
.collapse-button {
  display: inline-block;
  margin: 3px 40px 3px 5px;
  width: 18px;
  height: 18px;
  background: #eee;
  border-radius: 100%;
  text-align: center;

  &::before {
    content: '-';
  }
}

.icon {
  position: absolute;
  display: block;
  top: $icons-top;
  width: $icons-size;
  height: $icons-size;
}

#window1 {
  left: 20px;
  top: 40px;
  width: 320px;
  height: 280px;
}
#window1 .bar, #lb1 {
  background: skyblue;
}
#window2 {
  left: 200px;
  top: 250px;
  width: 250px;
  height: 150px;
}
#window2 .bar, #lb2 {
  background: pink;
}

@for $i from 1 through length($icons-left) {
  $icon-left: nth($icons-left, $i);
  $filter:
    url('https://gist.github.com/xl1/3785245/raw/f914e321b261b560a78f454246dede1c3aafe4b3/genie.vs')
    $default-fragment-shader,
    16 16,
    target num($icon-left + $icons-size / 2) num($icons-top);

  #wd#{$i} {
    -webkit-filter: custom($filter, timing 1);
    filter: custom($filter, timing 1);
    #wd#{$i}-open:checked ~* & {
      -webkit-filter: custom($filter, timing 0);
      filter: custom($filter, timing 0);
    }
  }
  #lb#{$i} {
    left: $icon-left;
  }
}
