css.content:
/* #! scss */
$vendor-prefixes: '-webkit-' '-moz-' '-ms-' '-o-' '' !default;

@mixin vendor($list) {
  @if length(nth($list, 1)) != 2 { $list: append((), $list); }
  @each $l in $list {
    $prop: nth($l, 1);
    $value: nth($l, 2);
    @each $p in $vendor-prefixes {
      #{$p + $prop}: $value;
    }
  }
}

@mixin vendor-prop($prop, $value) {
  @each $p in $vendor-prefixes {
    #{$prop}: #{ $p + $value };
  }
}


$rail-box-size: 75px;
$rail-width: 25px;
$rail-color: #29f;

$rail-left: ($rail-box-size - $rail-width) / 2;
$rail-right: $rail-box-size - $rail-left;

input {
  position: absolute;
  z-index: 2;
  width: $rail-width;
  height: $rail-width;
  margin: 0;
  left: 0;
  top: $rail-box-size + $rail-left;

  &:first-of-type {
    left: $rail-left;
    top: auto;
    bottom: - $rail-box-size;
  }
  &:last-of-type {
    left: auto;
    right: 0;
    top: $rail-box-size + $rail-left;
  }
}


.rail, #top {
  position: absolute;
  left: 0;
  top: 0;
  width: $rail-box-size;
  height: $rail-box-size;
}

// start point
#top {
  left: 200px;
  top: 90px;
}

.rail::after {
  content: '';
  position: absolute;
  left: $rail-box-size / 2 - $rail-width / 4;
  top: $rail-box-size;
  width: $rail-width / 2;
  height: $rail-width / 4;
  background-color: $rail-color;
}

$color-stops: transparent $rail-left, $rail-color $rail-left, $rail-color $rail-right, transparent $rail-right;

input:checked + .rail {
  @include vendor(
    (transform, translateY($rail-box-size) rotate(-90deg))
  );
  @include vendor-prop(background,
    radial-gradient(100% 100%, circle, $color-stops)
  );
}
input:checked +*+ .rail {
  @include vendor(
    (transform, translateY($rail-box-size) rotate(90deg))
  );
  @include vendor-prop(background,
    radial-gradient(0 100%, circle, $color-stops)
  );
}
input:checked +*+*+ .rail {
  @include vendor(
    (transform, translateY($rail-box-size))
  );
  @include vendor-prop(background, linear-gradient(0deg, $color-stops));
  background: linear-gradient(90deg, $color-stops);
}
