css.content:
/* #! scss */
$vendor-prefixes: '-webkit-' '-moz-' '-ms-' '-o-' '' !default;

@mixin vendor($list) {
  @if length(nth($list, 1)) != 2 { $list: append((), $list); }
  @each $l in $list {
    $prop: nth($l, 1);
    $value: nth($l, 2);
    @each $p in $vendor-prefixes {
      #{$p + $prop}: $value;
    }
  }
}

@mixin vendor-prop($prop, $value) {
  @each $p in $vendor-prefixes {
    #{$prop}: #{ $p + $value };
  }
}


// force to redraw (for Chrome)
@mixin redraw($target: body) {
  @-webkit-keyframes redraw {
    from { min-width: 1px; }
    to { min-width: 2px; }
  }
  #{$target} {
    -webkit-animation: redraw 1s infinite;
  }
}


// parallax scrolling effect
$parallax-field-height: 100%;
@mixin use-parallax($height) {
  $parallax-field-height: $height;
  body {
    height: $height;
    margin: 0;
    overflow-x: hidden;
  }
}
.parallax-sticky-outer {
  position: absolute;
  top: 0;
  @include vendor((transform-origin, 0 0));
  >* {
    @include vendor-prop(position, sticky);
    top: 0;
    @include vendor((transform-origin, 0 0));
  }
}
@mixin parallax-outer($start: 0, $speed: -1) {

  @if $speed == -1 {
    // static
    position: absolute;
    top: $start;
  } @else if $speed == 0 {
    // fixed
    position: fixed;
    top: $start;
  } @else {
    // sticky
    @extend .parallax-sticky-outer;

    $scale: 1 + $speed;
    height: abs($parallax-field-height / $scale);

    @if $speed < -1 {
      @include vendor(
        (transform, translate(100%, $parallax-field-height) scale($scale))
      );
      >* {
        @include vendor((transform, scale(1 / $scale)
          translate(-100%, - $parallax-field-height) translateY($start)));
      }
    } @else {
      @include vendor((transform, scale($scale)));
      >* {
        @include vendor((transform, scale(1 / $scale) translateY($start)));
      }
    }
  }
}


// main
$vendor-prefixes: '-webkit-' '';

@include redraw;
@include use-parallax($height: 1500px);

body {
  background: url('../assets/pUsSW.gif');
}

.block {
  width: 40px;
  height: 40px;
}

@for $i from 0 through 10 {
  #par#{$i} {
    $speed: $i * 0.5 - 2.5;
    @include parallax-outer(200px, $speed);
    left: $i * 40px;
    > .block {
      background: hsl($i * 32, 100%, 70%);
      &::before {
        content: '#{$speed}';
      }
    }
  }
}
