/* #! scss */
@function int($x) {
  $list: ();
  @for $i from floor($x - $x) to floor($x) {
    $list: append($list, 0);
  }
  @return length($list);
}

#a, #b, #c {
  height: 30px;
}
#a::before { content: '#{int(1px)}'; }
#b::before { content: '#{int(40em)}'; }
#c::before { content: '#{int(23.4%)}'; }
