// forked from svggirl's "ShuffleColorFilter" http://jsdo.it/svggirl/svg_sample_filter

if(!filter) var filter = {};
filter.dummy = 0;
$('<input type="range" max="20" min="1" step="0.1">')
    .attr('value', '10')
    .css({
        'position': 'absolute',
        'left': '0',
        'top': '50%',
        'width': '100%',
        'height': '30px',
        'margin': '-15px 0',
        'z-index': '9999'
    })
    .change(function(){ config.fps = this.value -0; })
    .appendTo('body');

css.content:
html, body {
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
  overflow: hidden;
}

html {
  background-color: #fff;
}

#svg, #ext {
  position: absolute;
  top: 0;
  left: 0;
}

#ext div {
  display: none;
  position: absolute;
  top: 0;
  left: 0;
}

#ext div:first-child {
  display: block;
}

#svg_143_157_3_tower_svgz {
  z-index: 0 !important;
}

div {
  width: 100%;
  height: 100%;
}

svg {
  width: 100%;
  height: 100%;
  vertical-align: middle;
}
